package assignment_1;

class Q_2_Swap_no
{
	public static void main(String[] args) 
	{
		// TODO Auto-generated method stub
		int a=10;
		int b=20;
		int temp;
		{
			System.out.println("before swaping print value in temp = " +a+"  "+b );
		}
		temp=a;
		a=b;
		b=temp;
		{
			System.out.println("after swaping print value in temp = " +a+"  " +b  );
		}
	}

}
